mapper.select:
SELECT t.`id`, t.`school_id`, s.`name` AS schoolName, t.`name`, t.`quan_pin` AS quanPin, t.`gender`, t.`identity`, t.`account` AS account, t.`is_manage` AS isManage, t.`no`, t.`phone`, t.`mobile`, t.`email`, t.`bgsdh` as workPhone, t.`start_work` AS startWork, t.`create_by`, t.`create_date`, t.`update_by`, t.`update_date`, t.`remarks`, t.`del_flag` FROM user_teacher t,org_school s WHERE t.`school_id`=s.`id` and t.del_flag=0 AND t.`school_id`=#{schoolId}
AND t.`id`=#{teacherId}
AND t.`name` like #{teacherName}
ORDER BY t.`create_date` DESC

SELECT * from user_teacher t1
where t1.id in
(
select t2.teacher_id from ref_teacher_class t2
where t2.class_id = #{classId}
and t2.type = #{type}
)
and t1.del_flag = 0

SELECT * FROM user_teacher t WHERE t.name LIKE #{teacherName} AND t.del_flag=0

    AND school_id = #{schoolId}

ORDER BY t.`create_date` DESC

SELECT * FROM user_teacher

    WHERE 1=1

        AND school_id = #{schoolId}

    AND del_flag = 0 AND id IN

        #{id}

SELECT * FROM v_teacher_school v WHERE v.`delFlag`=0

    AND v.`schoolId` = #{schoolId}


    AND v.`schoolId` <> #{currentSchoolId} AND v.`schoolParentId` = #{currentSchoolId}

AND NAME LIKE #{teacherName}

SELECT t.`id`, t.`school_id`, s.`name` AS schoolName, t.`name`, t.`quan_pin` AS quanPin, t.`gender`, t.`identity`, t.`account` AS account, t.`is_manage` AS isManage, t.`no`, t.`phone`, t.`mobile`, t.`email`, t.`bgsdh` as workPhone, t.`start_work` AS startWork, t.`create_by`, t.`create_date`, t.`update_by`, t.`update_date`, t.`remarks`, t.`del_flag` FROM user_teacher t,org_school s WHERE t.`school_id`=s.`id` and t.del_flag=0 AND t.`school_id`=#{schoolId}
AND t.`id`=#{teacherId}
AND t.`name` like #{teacherName}
ORDER BY t.`create_date` DESC
